<?phpinclude '../Database/Connect.php';global $conn;session_start();if($_SERVER['REQUEST_METHOD'] === 'POST'){    $id = $_SESSION['status'];    $img = $_POST['img'];    $Product_t = $_POST['Product_t'];    $Price = $_POST['Price'];    $Quantity = $_POST['Quantity'];    $Query = "INSERT INTO cart_tb(Product_name,Quantity,price,image,customerid)         values('$Product_t','$Quantity','$Price','$img','$id')";    if(mysqli_query($conn,$Query)){        echo "Alert Success Added to the cart";    }else{        echo "error" .mysqli_error($conn);    }}