<?phpinclude '../Database/Connect.php';global $conn;session_start();if($_SERVER['REQUEST_METHOD'] === 'POST'){    $costumerId = $_SESSION['status'];    $Query = "SELECT Product_name, Quantity, price,customerid,image FROM cart_tb WHERE customerid = $costumerId";    $RESULT = mysqli_query($conn,$Query);    $arrayData = array(); // declare the array variable as empty    while ($row = mysqli_fetch_assoc($RESULT)){        $arrayData[] = $row; // assigning the data from cart table into array data    }    // loop the array    foreach ($arrayData as $Data){        $Product_name = $Data['Product_name'];        $Quantity = (int) $Data['Quantity'];        $price = (float) $Data['price'];        $customerid =(int) $Data['customerid'];        $image = $Data['image'];        $Total =  $price * $Quantity;        echo number_format($Total, 2);        /// insert the data into orders table        $Query  = "INSERT INTO orders(Product_name,Image,customerid,Total_price,Quantitiy)        values ('$Product_name','$image','$customerid','$Total','$Quantity')";        mysqli_query($conn,$Query); //execute the Query    }    $Query  = "DELETE FROM cart_tb WHERE customerid = $costumerId";    mysqli_query($conn,$Query);    $conn->close();}