<?phpinclude '../Database/Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $UserName = $_POST['UserName'];    $Password = $_POST['Password'];    if(empty($Password) || empty($UserName)){        echo "<span class='alert alert-danger' >Please fill the inputs</span>";    }else{        $passHash = md5($Password);        $Query = "INSERT INTO customer(UserName,Password) values ('$UserName','$passHash')";        if(mysqli_query($conn,$Query)){            echo "Successfully Registered";        }else{            echo "<span class='alert alert-warning'>UserName is Already Exist!</span>";        }    }}