<?phpinclude '../Database/Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $filePath = $_FILES['Image']['tmp_name'];    $fileName =  $_FILES['Image']['name'];    $file_dir = './Review_image_uploaded/';    $destination = $file_dir . $fileName;    move_uploaded_file($filePath,$destination);    $imageName = htmlspecialchars(basename($_FILES['Image']['name']));    $Message = $_POST['Message'];    $costumerId = $_POST['CostumerId'];    $Query = "INSERT INTO reviews(image,Message,customerid) values('$imageName','$Message','$costumerId')";    mysqli_query($conn,$Query);    $conn->close();}